import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import events from '../mockData';

const EventDetailPage = () => {
  const { id } = useParams();
  const selectedEvent = events.find((event) => event.id === parseInt(id));
  const [editMode, setEditMode] = useState(false);
  const navigate = useNavigate(); // Use useNavigate instead of useHistory

  const handleDelete = () => {
    // Implement deletion logic
    const updatedEvents = events.filter((event) => event.id !== parseInt(id, 10));
    console.log('Deleted Event:', selectedEvent);
    console.log('Updated Events:', updatedEvents);

    // Update your events state here
    setEvents(updatedEvents); // Assuming you have a state variable for events
    navigate('/'); // Navigate back to the event list
  };

  const handleUpdate = () => {
    // Implement update logic
    const updatedEvents = events.map((event) =>
      event.id === parseInt(id, 10) ? updatedEvent : event
    );
    console.log('Updated Event:', selectedEvent);
    console.log('Updated Events:', updatedEvents);

    // Update your events state here
    setEvents(updatedEvents); // Update the state with the updated event
    setEditMode(false); // Exit edit mode
  };


  return (
    <div>
      <h2>Event Detail</h2>
      {selectedEvent ? (
        <div>
          <p>
            <strong>Name:</strong> {selectedEvent.name}
          </p>
          <p>
            <strong>Date:</strong> {selectedEvent.date}
          </p>
          <p>
            <strong>Location:</strong> {selectedEvent.location}
          </p>
          <p>
            <strong>Description:</strong> {selectedEvent.description}
          </p>
          <p>
            <strong>Organizer:</strong> {selectedEvent.organizer}
          </p>
          {editMode ? (
            <div>
              {/* Add form fields for editing */}
              <button onClick={handleUpdate}>Update</button>
            </div>
          ) : (
            <div>
              <button onClick={handleDelete}>Delete</button>
              <button onClick={() => setEditMode(true)}>Edit</button>
            </div>
          )}
        </div>
      ) : (
        <p>Event not found</p>
      )}
      <Link to="/">Back to Event List</Link>
    </div>
  );
};

export default EventDetailPage;
