import React, { useState } from 'react';
import { useParams} from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

import events from '../mockData';

const EventDetail = () => {
  const { id } = useParams();
  const selectedEvent = events.find((event) => event.id === parseInt(id));
  const [editMode, setEditMode] = useState(false);
  const navigate = useNavigate();


  const handleDelete = () => {
    // Implement deletion logic
    const updatedEvents = events.filter((event) => event.id !== parseInt(id));
    console.log('Deleted Event:', selectedEvent);
    console.log('Updated Events:', updatedEvents);

    // You may want to update your state or send a request to a server to update data
    // For now, let's navigate back to the event list
    // silinen kod
    //history.push('/');
  };

  const handleUpdate = () => {
    // Implement update logic
    // For demonstration purposes, let's assume you have a form with updatedEvent state
    // Update the selectedEvent with the form data or make a request to update on the server
    console.log('Updated Event:', selectedEvent);
    setEditMode(false);

    // You may want to update your state or send a request to a server to update data
  };

  return (
    <div>
      <h2>Event Detail</h2>
      {selectedEvent ? (
        <div>
          <p>
            <strong>Name:</strong> {selectedEvent.name}
          </p>
          <p>
            <strong>Date:</strong> {selectedEvent.date}
          </p>
          <p>
            <strong>Location:</strong> {selectedEvent.location}
          </p>
          <p>
            <strong>Description:</strong> {selectedEvent.description}
          </p>
          <p>
            <strong>Organizer:</strong> {selectedEvent.organizer}
          </p>
          {editMode ? (
            <div>
              {/* Add form fields for editing */}
              <button onClick={handleUpdate}>Update</button>
            </div>
          ) : (
            <div>
              <button onClick={handleDelete}>Delete</button>
              <button onClick={() => setEditMode(true)}>Edit</button>
            </div>
          )}
        </div>
      ) : (
        <p>Event not found</p>
      )}
    </div>
  );
};

export default EventDetail;
